INSERT INTO reviews (booking_id, item_type, item_id, customer_name, customer_email, rating, title, comment, verified) VALUES
('850e8400-e29b-41d4-a716-446655440001', 'Cruise', '650e8400-e29b-41d4-a716-446655440001', 'Rahul Gupta', 'rahul.gupta@email.com', 5, 'Absolutely Amazing Experience', 'The Royal Caribbean Explorer exceeded all our expectations. The staff was incredibly friendly, the food was delicious, and the entertainment was world-class. The ocean view cabin was perfect for our romantic getaway. Highly recommend!', true),
('850e8400-e29b-41d4-a716-446655440002', 'Hotel', '750e8400-e29b-41d4-a716-446655440001', 'Priya Mehta', 'priya.mehta@email.com', 4, 'Luxury at its Best', 'The Oberoi Mumbai is truly a luxury experience. The rooms are spacious and beautifully designed. The service was impeccable, though the late checkout took some time to arrange. Overall, a wonderful stay.', true),
('850e8400-e29b-41d4-a716-446655440006', 'Hotel', '750e8400-e29b-41d4-a716-446655440003', 'Deepika Rao', 'deepika.rao@email.com', 4, 'Great Business Hotel', 'ITC Grand Chola is perfect for business travelers. The business center facilities are excellent and the location is convenient. The room service could be faster, but overall a good experience.', true),
('850e8400-e29b-41d4-a716-446655440007', 'Cruise', '650e8400-e29b-41d4-a716-446655440009', 'Neha Patel', 'neha.patel@email.com', 5, 'Perfect Anniversary Celebration', 'Anthem of the Seas made our anniversary unforgettable. The ship is massive with so many activities. The robot bartenders were a fun experience! The staff went above and beyond to make our celebration special.', true),
('850e8400-e29b-41d4-a716-446655440009', 'Cruise', '650e8400-e29b-41d4-a716-446655440004', 'Sanjay Verma', 'sanjay.verma@email.com', 3, 'Good but Room for Improvement', 'Princess Sapphire is a beautiful ship with great amenities. However, the vegetarian meal options were limited despite our special request. The Lakshadweep destination was breathtaking though.', true),
('850e8400-e29b-41d4-a716-446655440010', 'Hotel', '750e8400-e29b-41d4-a716-446655440005', 'Meera Krishnan', 'meera.krishnan@email.com', 5, 'Paradise in Goa', 'Goa Marriott Resort is absolutely stunning! The beach view from our room was incredible. The staff was very helpful and the facilities were top-notch. Perfect for a romantic getaway.', true),
('850e8400-e29b-41d4-a716-446655440011', 'Cruise', '650e8400-e29b-41d4-a716-446655440010', 'Anand Iyer', 'anand.iyer@email.com', 4, 'Ultra-Luxury Experience', 'Seabourn Encore is the epitome of luxury cruising. The butler service was exceptional and the dining was Michelin-star quality. The only issue was the butler service took some time to get arranged initially.', true),
('850e8400-e29b-41d4-a716-446655440012', 'Hotel', '750e8400-e29b-41d4-a716-446655440010', 'Pooja Agarwal', 'pooja.agarwal@email.com', 4, 'Modern Luxury in Bangalore', 'Four Seasons Bengaluru offers contemporary luxury with excellent service. The spa facilities are world-class. The airport transfer arrangement had some delays, but the hotel staff handled it professionally.', true)
