-- Update user statistics based on bookings
UPDATE users SET 
  total_bookings = (
    SELECT COUNT(*) FROM bookings 
    WHERE agent_id = users.id AND status != 'Cancelled'
  ),
  total_revenue = (
    SELECT COALESCE(SUM(total_amount), 0) FROM bookings 
    WHERE agent_id = users.id AND status != 'Cancelled'
  );

-- Update cruise and hotel ratings based on reviews
UPDATE cruises SET 
  rating = (
    SELECT COALESCE(AVG(rating), 0) FROM reviews 
    WHERE item_id = cruises.id AND item_type = 'Cruise' AND status = 'Published'
  ),
  review_count = (
    SELECT COUNT(*) FROM reviews 
    WHERE item_id = cruises.id AND item_type = 'Cruise' AND status = 'Published'
  );

